ARGV[0] = "--help" if ARGV[0] == nil

opts = GetoptLong.new(
  [ '--help', '-h', GetoptLong::NO_ARGUMENT ],
  [ '--shift', '-s', GetoptLong::REQUIRED_ARGUMENT],
  [ '--func', '-f', GetoptLong::REQUIRED_ARGUMENT],
  [ '--words', '-w', GetoptLong::REQUIRED_ARGUMENT],
  [ '--type', '-t', GetoptLong::OPTIONAL_ARGUMENT],
  [ '--reverse', '-r', GetoptLong::NO_ARGUMENT],
  [ '--removeletters', '-l', GetoptLong::NO_ARGUMENT],
  [ '--brutedict', '-b', GetoptLong::NO_ARGUMENT],
  [ '--reversemd5', '-m', GetoptLong::NO_ARGUMENT],
  [ '--base64', '-1', GetoptLong::REQUIRED_ARGUMENT]
)

opts.each do |opt, arg|
  case opt
    when '--help'
      helper = "\e[1;34mA858\e[0m\ \n"
      helper << "\e[1;34m=================================\e[0m\ "
      helper << %q[
-h, --help:
   show help

-s, --shift
    Value to shift base code

-f, --func
    Mathematical function to use
    
    addition
    subtract
    multiply
    divide

-t, --type
    Baseshift Return value in
    
    chr, ord, hex

-w, --words
    Words to process

-r, --reverse
    Reverse each 32 chr string

-m, --reversemd5
    Reverse MD5 string against dictionary
    
-l, --removeletters
    Remove letters from 32 chr hex string

-b, --brutedict
    Load dictionary file

-1, --base64
    encode / decode
 ]
      puts helper
      exit
    when '--shift'
      @shift = arg.to_i
    when '--func'
      @func = arg.to_s
    when '--type'
      @type = arg.to_s
    when '--words'
      @wordsnum = arg.to_i
    when '--reverse'
      @reverse = arg.to_s
    when '--removeletters'
      @removeletters = arg.to_s
    when '--brutedict'
      @brutedict = arg.to_s
    when '--reversemd5'
      @reversemd5 = arg.to_s
    when '--base64'
      @base64 = arg.to_s
  end
end

class MyCipher

=begin

OpenSSL::Cipher::AES::AES128.ciphers.each {|n| puts n }

Supported 128bit ciphers

aes-128-cbc
aes-128-cfb
