aes-128-ecb
aes-128-gcm
aes-128-ofb
aes-128-xts

camellia-128-cbc
camellia-128-cfb
camellia-128-cfb1
camellia-128-cfb8
camellia-128-ecb
camellia-128-ofb

=end

  def initialize(method, cipher="aes-128-cbc")
    @cipher = OpenSSL::Cipher::Cipher.new("#{cipher}")
    case method
    when "encrypt"
      @cipher.encrypt
    when "decrypt"
      @cipher.decrypt
    end
  end

  # Need to save your keys somewhere safe for later
  def generate_iv(passphrase)
    key = Digest::SHA1.hexdigest("#{passphrase}")
    iv = @cipher.random_iv
    return [key, iv]
  end
  
  # Specify your Key / IV so you can Encrypt / Decrypt
  def set_iv(key, iv)
    @cipher.key = key
    @cipher.iv = iv
  end
  
  def encypt(msg)
    encrypted = @cipher.update("#{msg}")
    encrypted << @cipher.final
  end
  
  def decrypt(msg)
    decrypted = cipher.update("#{msg}")
    decrypted << cipher.final
  end

end

class A858

  attr_writer :reverse, :reversemd5, :removeletters, :brutedict, :dictionary, :base64
  
  def initialize
    @twochar_col = Array.new
    @dehex_col = Array.new
    @chr_col = Array.new
    @baseshift = Array.new
    @reverse = false
    @reversemd5 = false
    @removeletters = false
    @brutedict = false
    @dictionary = "wordsEn.txt"
    @base64 = false
    @debug = 0
  end
  
  def md5sum(dictline)
    md5 = Digest::MD5.new
    md5 << dictline
    md5.hexdigest
  end
  
  def load_dict(str)
    if @reversemd5 == true; str.reverse!; end
    File.open("#{@dictionary}", 'r') {|t|
      t.each_line {|l|
        if md5sum(l) == str
          puts "Wow a match: #{str}"
        end
      }
    }
    if defined?(res)
      return str
    else
      return false
    end
  end

  def self.define_component(name)
    name_func = name.to_s.split("_")[1].to_sym
    define_method(name) do |val|  
      (val).send("#{name_func}")
    end
  end

  define_component :convert_hex
  define_component :convert_chr
  define_component :convert_ord
  
  def baseshift(val, shift, func, type="chr")
    puts "Params: #{val} Shift: #{shift} Func: #{func} Type: #{type}" if @debug > 2
    if func == "subtract"
      a = val - shift
    elsif func == "addition"
      a = val + shift
    elsif func == "divide"
      a = val / shift
    elsif func == "multiply"
      a = val * shift
    end
    puts "A Value: #{a}" if @debug > 3
    # Return Type
    begin
      case type
      when "chr"
        return a.chr
      when "ord"
        return a.ord
      when "hex"
        return a.hex
      end
    rescue RangeError => e
      #pp e
    end
  end
  
  def decode(str, wordsnum, shift, func, type="chr")
    mdtuples = str.split(" ")
    twochar = String.new
    c = 0
    k, l, h = Keycounter.new, Keycounter.new, Keycounter.new
    print "Twochar:"
    mdtuples.each {|n| # 32 Char strings of which last is 16
      n.reverse! if @reverse == true
      n.gsub!(/[a-f]/, "") if @removeletters == true
      if @base64 != false
        if @base64 == "encode"
          n = Base64.strict_encode64(n) 
        else
          n = Base64.strict_decode64(n)
        end
      end
      if @brutedict == true; Thread.new { load_dict(n) }; end
      n.split("").each {|m|
        twochar << m
        if twochar.size == 2
          print " #{twochar}"
          @twochar_col << twochar
          @dehex_col << convert_hex("#{twochar}")
          if @base64 == false; k.keycount("A858_#{@dehex_col[@dehex_col.size - 1]}"); end # Base64 breaks my counter oh well
          @chr_col << convert_chr(convert_hex("#{twochar}"))
          @baseshift << baseshift(convert_hex("#{twochar}"), shift, func, type)
          twochar = ""
        end
      }
      if c == wordsnum
        break;
      end
      if @base64 == false; l.keycount("A858_#{n}"); end
      c += 1
    }
    puts "\n\nMDTuples: #{mdtuples.size}"
    puts "Two Char: #{@twochar_col}"
    puts "Dehex Char: #{@dehex_col}"
    puts "Chr: #{@chr_col}"
    puts "Baseshift Char: #{@baseshift}"
    keys =  k.keycount_compile
    puts "Keycounter Codes info: "
    pp keys.sort_by { |h| h[1] }
    keys.sort_by { |i|
      h.keycount("A858_REP_#{i[1]}")
    }
    puts "Keycounter Codes - Repeat totals occurances: "
    repeat = h.keycount_compile
    pp repeat.sort_by { |h| h[1] }
    puts "Keycounter Code info Size:"
    pp keys.sort_by { |h| h[1] }.size
    #puts "Keycounter Code Left Column Unique: "
    #pp keys.sort.uniq { |h| h[0] }
    #puts "Keycounter Code Left Column Unique Size: "
    #pp keys.sort.uniq { |h| h[0] }.size
    #strings = l.keycount_compile
    #pp strings.sort_by { |h| h[1] }
  end

end

